import WeatherCard from '../components/layout/WeatherCard';

import { useJsApiLoader, Autocomplete,DirectionsRenderer,GoogleMap,Marker } from '@react-google-maps/api';
import { useState,useRef } from 'react';



const containerStyle = {
    width: '100%',
    height: '100%'
  };
  

const center = {
    lat: 53.3463,
    lng: -6.2631
  };
    


function JourneyPlanning() {
    const [directionsResponse, setDirectionsResponse] = useState(null)
   
    /**@type React.MutableRefObject<HTMLInputElement> */
    const startRef = useRef()
    /**@type React.MutableRefObject<HTMLInputElement> */
    const destinationRef = useRef()
    const [map, setMap] = useState(/** @type google.maps.Map */ (null))
    const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: "AIzaSyCdf-x6SluXsWzP9qpwxVGBY08pm_3TAQU",
    libraries:['places']
  })
      if(!isLoaded) {
        return "map is not loaded";
    }

    async function caculateRoute(){
        if(startRef.current.value === '' || destinationRef.current.value === '') {
            return;
        }
        // eslint-disable-next-line
        const directionsService = new google.maps.DirectionsService()
        // eslint-disable-next-line
        const results = await directionsService.route({
            origin: startRef.current.value,
            destination: destinationRef.current.value,
            // eslint-disable-next-line
            travelMode: google.maps.TravelMode.TRANSIT
        })
        setDirectionsResponse(results)
        console.log(results)
    }

// const markers =[]

//     async function StopFetch() {
//         const Stopdata = await fetch('http://localhost:8000/api/Stops/');
//         // waits until the request completes...
//         console.log(Stopdata);
//         const markers = Stopdata.json();
//       }

//       StopFetch().then(markers => {
//         markers; // fetched movies
//       });
const markers = async function exampleFetch() {
    const response = await fetch('http://localhost:8000/api/Stops/');
    const markers = await response.json();
    console.log(markers);
    exampleFetch();
}



   function clearRoute() {
    if(startRef.current.value === '' || destinationRef.current.value === '') {
        setDirectionsResponse(null);
    }
   }



//    let markers = [ // Just an example this should probably be in your state or props. 
//    {
//      name: "marker1",
//      position: { lat: 53.17655, lng: -6.138564 }
//    },
//    {
//      name: "marker2",
//      position: { lat: 53.16659, lng: -6.113569 }
//    },
//    {
//      name: "marker3",
//      position: { lat: 49.15659, lng: -123.143569 }
//    }
//  ];
//  const res = fetch('http://localhost:8000/api/Stops/');
//  const markers = res.json();


    return  (
    <><div className='weather-card'><WeatherCard /></div>
    <div className="flex-container">
        <div className="box1">
            <h1>Journey Planner</h1>
            <div className="journey-form">
                <Autocomplete>
                <input type="search" placeholder="Start Point" className="box" ref={startRef}></input>
                </Autocomplete>
                <Autocomplete>
                <input type="search" placeholder="Destination" className="box" ref={destinationRef}></input>
                </Autocomplete>
                <label for="time">Choose a time to start the journey: </label>
                <select id="option" onChange={() => {
                    var option = document.getElementById("option").value;
                    if (option !== "now") {
                        
                        document.getElementById("time").innerHTML = '<input type="date" name="" class="date">' + '<input type="time" name="" class="time">';
                    } else {
                        document.getElementById("time").innerHTML = "";
                    }
                } }>
                    <option value="now">Leave Now</option>
                    <option value="depart">Depart At</option>
                    <option value="arrive">Arrive By</option>
                    <option value="lastAvaliable">Last Avaliable</option>
                </select>

                <div id="time"></div>
                <button type="submit"  className="btn" onClick={caculateRoute}>Search</button>
            </div>
            <div id="result"></div>
        </div>
        <div className="box2">
            <div id="map">
            
                <GoogleMap
                    mapContainerStyle={containerStyle}
                    center={center}
                    zoom={13}
                    onLoad={map => setMap(map)}
                    >
                    { /* Child components, such as markers, info windows, etc. */ }
                   
                      {markers.map((marker, index) => (
                     <Marker
                    key={index}
                    name={marker.name}
                    position={{ lat:markers.latitude, lng:markers.longitude  }}
                     />
                     ))}
                    {directionsResponse && (<DirectionsRenderer directions={directionsResponse} />)}
                 </GoogleMap>
            </div>
        </div>
        
    </div></>);}
export default JourneyPlanning;
